import React, { useEffect, useState } from 'react'
import { useParams } from 'react-router-dom';
import { Movie } from '../interface/Movie';
export function MovieView() {
  const [movie, setMovie] = useState<Movie>();
  let { id } = useParams();
  useEffect(() => {
    fetch("https://localhost:7121/api/movies/" + id, {
      method: "GET"
    })
      .then(response => response.json())
      .then(data => {
        setMovie(data);
        console.log(data);
      })
  }, [])
  return (
    <div className="container mx-auto mt-10">
      <div className="max-w-l bg-white rounded-lg shadow-md dark:bg-gray-800 dark:border-gray-700">
        <a href="#">
          <img className="object-cover h-96 rounded-t-lg md:w-198 md:rounded-none md:rounded-l-lg" src={movie?.imageLinks[0]} alt="product image" />
        </a>
        <div className="px-5 pb-5">
          <a href="#">
            <h5 className="text-xl font-semibold tracking-tight text-gray-900 dark:text-white">Apple Watch Series 7 GPS, Aluminium Case, Starlight Sport</h5>
          </a>

          <div className="flex justify-between items-center">
            <span className="text-3xl font-bold text-gray-900 dark:text-white">$599</span>
            <a href="#" className="text-white bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:outline-none focus:ring-blue-300 font-medium rounded-lg text-sm px-5 py-2.5 text-center dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800">Add to cart</a>
          </div>
        </div>
      </div>
    </div>
  )
}
