import React, { useCallback, useState, useEffect } from 'react'
import { MovieCard } from '../components/main/MovieCard';
interface Movie {
    id: number;
    title: string;
    length: number;
    rating: number;
    imdbLink: string;
    imageLinks: Array<string>;
    people: Array<Object>;
    genres: Array<Object>;
    clickedCounter: number;
}
export function Home() {
    const [movies, setMovies] = useState<Array<Movie>>([]);
    useEffect(() => {
        fetch("https://localhost:7121/api/movies/")
            .then(response => response.json())
            .then(data => {
                setMovies(data);
            })
    }, [movies])
    const catalogue = movies.map((movie) =>
        <MovieCard movie={movie} />
    );
    return (
        <div>
            {catalogue}
        </div>
    )
}
