import React from 'react'
import { Link } from 'react-router-dom'
interface Movie {
    id: number;
    title: string;
    length: number;
    rating: number;
    imdbLink: string;
    imageLinks: Array<string>;
    people: Array<Object>;
    genres: Array<Object>;
    clickedCounter: number;
    description: string;
}
export function MovieCard(
    { movie }: { movie: Movie }) {
    return (
        <Link to="#" className="flex flex-col mt-2 items-center bg-white rounded-lg border shadow-md md:flex-row md:max-w-xl hover:bg-gray-100 dark:border-gray-700 dark:bg-gray-800 dark:hover:bg-gray-700">
            <img className="object-cover h-96 rounded-t-lg md:h-auto md:w-48 md:rounded-none md:rounded-l-lg" src={movie.imageLinks[0]} alt="" />
            <div className="flex flex-col justify-between p-4 leading-normal">
                <h5 className="mb-2 text-2xl font-bold tracking-tight text-gray-900 dark:text-white">{movie.title}</h5>
                <p className="mb-3 font-normal text-gray-700 dark:text-gray-400">{movie.description}</p>
                <div className="flow-root">
                    <ul role="list" className="divide-y divide-gray-200 dark:divide-gray-700">
                        <li className="py-3 sm:py-4">
                            <div className="flex items-center space-x-4">
                                <div className="flex-1 min-w-0">
                                    <p className="text-sm font-medium text-gray-900 truncate dark:text-white">
                                        Neil Sims
                                    </p>
                                    <p className="text-sm text-gray-500 truncate dark:text-gray-400">
                                        email@windster.com
                                    </p>
                                </div>

                                <div className="flex items-center mb-5">
                                    <p className="bg-blue-100 text-blue-800 text-sm font-semibold inline-flex items-center p-1.5 rounded dark:bg-blue-200 dark:text-blue-800">8.7</p>
                                    <p className="ml-2 font-medium text-gray-900 dark:text-white">Excellent</p>
                                    <span className="w-1 h-1 mx-2 bg-gray-900 rounded-full dark:bg-gray-500"></span>
                                    <p className="text-sm font-medium text-gray-500 dark:text-gray-400">376 reviews</p>
                                    <Link0 href="#" className="ml-auto text-sm font-medium text-blue-600 hover:underline dark:text-blue-500">Read all reviews</Link0>
                                </div>

                            </div>
                        </li>
                    </ul>
                </div>
            </div>

        </Link>

    )
}
